mapper.select:
SELECT dm.branchid,dl.dl_number,dm.branchname,dl.dl_date,c.c_contacts,ws.wname,dl.dl_yfje,dl.dl_sfje,dl.dl_qkje,dl.dl_yhje,ag.agent_name,su.user_name,dls.dl_statie_name,dlt.dl_type_name,dl.dl_beizhu FROM document_list dl LEFT JOIN customer c ON dl.customerid = c.c_id LEFT JOIN warehouse ws ON dl.wid = ws.wid LEFT JOIN agent ag ON dl.agent_id = ag.agent_id LEFT JOIN sys_user su ON dl.admin = su.user_id LEFT JOIN document_list_static dls ON dl.dl_statie_id = dls.dl_statie_id LEFT JOIN document_list_type dlt ON dl.dl_type_id = dlt.dl_type_id LEFT JOIN branch dm ON dm.branchid = c.branchid
dls.dl_statie_name = '销售未审核' AND dlt.dl_type_name = '销售出货'
and dl.dl_date between #{qishitime} and #{zuihotime}
and c.branchid = #{branchid}
and c_id = #{customerid}
and su.user_id = #{userid}
and dl.dl_number like concat('%',#{gjz},'%') or c.c_contacts like concat('%',#{gjz},'%')

select
id, dl_number, dl_date, supplierid, wid, dl_yfje, dl_sfje, dl_qkje, dl_yhje, agent_id, admin, dl_type_id,
dl_statie_id, dl_beizhu
from projects.document_list


        and dl_number = #{dlNumber}


        and dl_date = #{dlDate}


        and supplierid = #{supplie`rid}


        and wid = #{wid}


        and dl_yfje = #{dlYfje}


        and dl_sfje = #{dlSfje}


        and dl_qkje = #{dlQkje}


        and dl_yhje = #{dlYhje}


        and agent_id = #{agentId}


        and admin = #{admin}


        and dl_type_id = #{dlTypeId}


        and dl_statie_id = #{dlStatieId}


        and dl_beizhu = #{dlBeizhu}

SELECT l.dl_number,l.dl_date,a.agent_name,w.wname,lt.dl_type_name,l.dl_ysje,l.dl_ssje,u.user_name,c.c_contacts,l.dl_beizhu  FROM xsdocument_list l
LEFT JOIN agent a ON l.agent_id=a.id
LEFT JOIN warehouse w ON l.wid=w.wid
LEFT JOIN document_list_type lt ON l.dl_type_id=lt.dl_type_id
LEFT JOIN sys_user u ON l.admin=u.user_id
LEFT JOIN customer c ON l.customerid=c.c_id


    and agent_name = #{agentName}


    and wname = #{WName}


    and dl_type_name = #{dlTypeName}
