mapper.select:
select
id, dl_number, supplierid, wid, sp_shopid, sp_name, sp_Company, sp_jprice, number, loss_number, zje,
sp_Specifications, sp_color, beizhu
from projects.document_shop


        and id = #{id}


        and dl_number = #{dlNumber}


        and supperlierid = #{supplierid}


        and wid = #{wid}


        and sp_shopid = #{spShopid}


        and sp_name = #{spName}


        and sp_Company = #{spCompany}


        and sp_jprice = #{spJprice}


        and number = #{number}


        and loss_number = #{lossNumber}


        and zje = #{zje}


        and sp_Specifications = #{spSpecifications}


        and sp_color = #{spColor}


        and beizhu = #{beizhu}

SELECT sp.number,sp.loss_number,sp.zje,s.sp_shopid,s.sp_name,s.sp_Company,s.sp_Specifications,s.sp_color,s.sp_beizhu,l.dl_number,l.dl_date, lb.sp_type_name, w.wname,a.agent_name,sup.suppliername FROM document_shop sp LEFT JOIN shop s ON sp.sp_shopid=s.sp_shopid LEFT JOIN warehouse w ON sp.wid=w.wid LEFT JOIN document_list l ON sp.dl_number=l.dl_number LEFT JOIN supplier sup ON sp.supplierid=sup.s_id LEFT JOIN shop_type lb ON s.sp_type_id=lb.sp_type_id LEFT JOIN agent a ON l.agent_id=a.id
s.sp_name LIKE CONCAT('%',#{spName},'%')
and lb.sp_type_name = #{spTypeName}
