mapper.select:
select
sp_id, sp_shopid, sp_name, sp_bar_code, sp_type_id, sp_state, sp_Company, sp_Specifications, sp_color,
sp_jprice, sp_Preset_price, sp_Manufacturer, sp_beizhu, sp_time, sh_img
from projects.shop


        and sp_id = #{spId}


        and sp_shopid = #{spShopId}


        and sp_name = #{spName}


        and sp_bar_code = #{spBarCode}


        and sp_type_id = #{spTypeId}


        and sp_state = #{spState}


        and sp_Company = #{spCompany}


        and sp_Specifications = #{spSpecifications}


        and sp_color = #{spColor}


        and sp_jprice = #{spJprice}


        and sp_Preset_price = #{spPresetPrice}


        and sp_Manufacturer = #{spManufacturer}


        and sp_beizhu = #{spBeizhu}


        and sp_time = #{spTime}


        and sh_img = #{shImg}

SELECT sk.sp_Warehouse_id,s.`sp_shopid`,s.`sp_name`,s.`sp_Company`,s.`sp_Specifications`,s.`sp_color`,s.`sp_jprice`,SUM(sk.`sk_number`) AS`sk_number`,s.`sp_Manufacturer`,s.`sp_beizhu`,s.`sp_bar_code`,s.s_id FROM shop s LEFT JOIN shop_type tp ON s.sp_type_id = tp.sp_type_id LEFT JOIN stock sk ON s.sp_shopid = sk.sk_shopid
tp.sp_type_id = #{value}
GROUP BY s.`sp_shopid`

select s.sp_id, s.sp_shopid, s.sp_name, s.sp_bar_code, s.sp_type_id, s.sp_state, s.sp_Company, s.sp_Specifications, s.sp_color, s.sp_Preset_price, s.sp_jprice, s.sp_Manufacturer, s.sp_beizhu, s.sp_time, s.sh_img ,y.sp_type_id,y.sp_type_name from projects.shop_type y INNER JOIN projects.shop s on s.sp_type_id=y.sp_type_id
and sp_type_name = #{shoptypename}
and sp_state = #{shopstatic}
and sp_name like concat('%',#{shopname},'%') or sp_bar_code like concat('%',#{shopname},'%') or sp_shopid like concat('%',#{shopname},'%')

select s.sp_id, s.sp_shopid, s.sp_name, s.sp_bar_code, s.sp_type_id, s.sp_state, s.sp_Company, s.sp_Specifications, s.sp_color, s.sp_Preset_price, s.sp_jprice, s.sp_Manufacturer, s.sp_beizhu, s.sp_time, s.sh_img ,y.sp_type_id,y.sp_type_name from projects.shop_type y INNER JOIN projects.shop s on s.sp_type_id=y.sp_type_id
and sp_state = #{shopstatic}
and sp_type_name = #{shoptypename}
and sp_name like concat('%',#{shopname},'%') and sp_bar_code like concat('%',#{shopname},'%') or sp_shopid like concat('%',#{shopname},'%')

select s.sp_id, s.sp_shopid, s.sp_name, s.sp_bar_code, s.sp_type_id, s.sp_state, s.sp_Company, s.sp_Specifications, s.sp_color, s.sp_Preset_price, s.sp_jprice, s.sp_Manufacturer, s.sp_beizhu, s.sp_time, s.sh_img ,y.sp_type_id,y.sp_type_name from projects.shop_type y INNER JOIN projects.shop s on s.sp_type_id=y.sp_type_id
and sp_state = #{shopstatic}
and sp_type_name = #{shoptypename}
and sp_name like concat('%',#{shopname},'%') or sp_bar_code like concat('%',#{shopname},'%') or sp_shopid like concat('%',#{shopname},'%')

mapper.delete:
delete from projects.shop


        and sp_shopid in

            #{shopid}
