mapper.select:
select
s_id, supplierid, suppliername, contacts, contactnumber, suptypeid, addressid, payablemoney, companyid, money,
static, ismoren, lxaddress, cjtime, beizhu
from projects.supplier


        and s_id = #{sId}


        and supplierid = #{supplierid}


        and suppliername = #{suppliername}


        and contacts = #{contacts}


        and contactnumber = #{contactnumber}


        and suptypeid = #{suptypeid}


        and addressid = #{addressid}


        and payablemoney = #{payablemoney}


        and companyid = #{companyid}


        and money = #{money}


        and static = #{state}


        and ismoren = #{ismoren}


        and lxaddress = #{lxaddress}


        and cjtime = #{cjtime}


        and beizhu = #{beizhu}

SELECT su.s_id,su.supplierid,su.suppliername,su.contacts,su.contactnumber,su.suptypeid,st.supname, su.addressid,ad.address,su.payablemoney,su.companyid,b.branchname,su.money,su.static,su.ismoren, su.lxaddress,su.cjtime,su.beizhu FROM supplier su LEFT JOIN suppliertype st ON su.suptypeid=st.suptypeid LEFT JOIN address ad ON ad.addressid=su.addressid LEFT JOIN branch b ON b.branchid=su.companyid
and branchname = #{branchnames}
and supplierid like concat('%',#{suppliername},'%') or suppliername like concat('%',#{suppliername},'%') or address like concat('%',#{suppliername},'%')

mapper.delete:
delete from projects.supplier


        and s_id in

            #{sId}
